import React, { useState } from 'react';
import { useTable, useFilters, useGlobalFilter } from 'react-table';

import { Population, fetchData } from '../api';
import { JSX } from 'react/jsx-runtime';

const columns: any = [
  {
    Header: 'ID',
    accessor: 'id'
  },
  {
    Header: 'Country',
    accessor: 'country'
  },
  {
    Header: 'Year',
    accessor: 'year'
  },
  {
    Header: 'TotalMale',
    accessor: 'totalMale'
  },
  {
    Header: 'TotalFemale',
    accessor: 'totalFemale'
  }
];

export const PopulationGrid = () => {
  const [data, setData] = React.useState<Population[]>([]);

  const [filterInput, setFilterInput] = useState('');

  const GlobalFilter = ({ filter, setFilter }: any) => {
    return (
      <span>
        Search:{' '}
        <input
          value={filter || ''}
          onChange={(e) => setFilter(e.target.value || undefined)}
          placeholder="Type to search..."
        />
      </span>
    );
  };



  const handleFilterChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value
    setFilter('country', value);
    setFilterInput(value);
  };
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    state,
    //@ts-ignore
    setGlobalFilter,
  } = useTable(
    {
      columns,
      data,
    },
    useGlobalFilter
  );
  //@ts-ignore
  const { globalFilter } = state;


  React.useEffect(() => {
    fetchData().then(setData);
  }, []);

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <header style={{ background: '#333', padding: '10px', color: '#fff' }}>
        <h1>Population Data</h1>
        <nav>
          <a href="/population" style={{ color: '#fff', textDecoration: 'none' }}>Population</a>
          {/* Add more navigation links as needed */}
        </nav>
        <GlobalFilter filter={globalFilter} setFilter={setGlobalFilter} />
      </header>
      <div style={{ height: '80vh', width: '100%', overflowX: 'auto', overflowY: 'auto' }}>
        {/* Filter input field */}
        <label>
          Filter by Country:
          <input
            value={filterInput}
            onChange={handleFilterChange}
            placeholder={"Search for a country..."}
            style={{ margin: '10px' }}
          />
        </label>
        <table {...getTableProps()} style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            {headerGroups.map((headerGroup: { getHeaderGroupProps: () => JSX.IntrinsicAttributes & React.ClassAttributes<HTMLTableRowElement> & React.HTMLAttributes<HTMLTableRowElement>; headers: any[]; }) => (
              <tr {...headerGroup.getHeaderGroupProps()} style={{ background: '#F5F5F5' }}>
                {headerGroup.headers.map(column => (
                  <th
                    {...column.getHeaderProps()}
                    style={{
                      borderBottom: '1px solid #e0e0e0',
                      padding: '10px',
                      textAlign: 'left'
                    }}
                  >
                    {column.render('Header')}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map((row: any) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()} style={{ borderBottom: '1px solid #e0e0e0' }}>
                  {row.cells.map((cell: any) => (
                    <td
                      {...cell.getCellProps()}
                      style={{ padding: '10px' }}
                    >
                      {cell.render('Cell')}
                    </td>
                  ))}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <footer style={{ background: '#333', padding: '10px', color: '#fff', textAlign: 'center' }}>
        <p>&copy; {new Date().getFullYear()} Your Company Name. All rights reserved.</p>
      </footer>
    </div>

  );
};
