import React, { useEffect, useState } from 'react';
import { useTable, useSortBy, useFilters } from 'react-table';
import { Population, fetchData } from '../api';


type Column = {
  Header: string;
  accessor: keyof Population;
};

const columns: Column[] = [
  {
    Header: 'ID',
    accessor: 'id',
  },
  {
    Header: 'Country',
    accessor: 'country',
  },
  {
    Header: 'Year',
    accessor: 'year',
  },
  {
    Header: 'TotalMale',
    accessor: 'totalMale',
  },
  {
    Header: 'TotalFemale',
    accessor: 'totalFemale',
  }
];

export const PopulationGrid = () => {
  const [data, setData] = useState<Population[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortColumn, setSortColumn] = useState(''); // Default: empty
  const [sortDirection, setSortDirection] = useState('asc'); // Default: ascending


  const [sortBy, setSortBy] = useState('orderPlacedAt');
  const [sortIcon, setSortIcon] = useState('')


  const handleSort = (headerText: string) => {
    setSortBy(headerText);
    setSortDirection(sortDirection === 'ASC' ? 'DESC' : 'ASC');
  };

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow
  } = useTable(
    {
      columns,
      data
    },

    useFilters,

    useSortBy
  );

  useEffect(() => {
    fetchData(searchTerm, sortColumn, sortDirection).then(setData);
  }, [searchTerm, sortColumn, sortDirection]);

  // const handleSort = (columnName: string) => {
  //   if (sortColumn === columnName) {
  //     setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
  //   } else {
  //     setSortColumn(columnName);
  //     setSortDirection('asc');
  //   }
  // };

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <header style={{ background: '#333', padding: '10px', color: '#fff' }}>
        <h1>Population Data</h1>
        <nav>
          <a href="/population" style={{ color: '#fff', textDecoration: 'none' }}>Population</a>
          {/* Add more navigation links as needed */}
        </nav>
      </header>
      <input
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Search..."
      />

      <div style={{ height: '80vh', width: '100%', overflowX: 'auto', overflowY: 'auto' }}>
        <table {...getTableProps()} style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            {headerGroups.map((headerGroup) => (
              <tr {...headerGroup.getHeaderGroupProps()} style={{ background: '#F5F5F5' }}>
                {headerGroup.headers.map((column: any) => (
                  <th
                    {...column.getHeaderProps(column.getSortByToggleProps())}
                    style={{
                      borderBottom: '1px solid #e0e0e0',
                      padding: '10px',
                      textAlign: 'left',
                      cursor: 'pointer'
                    }}
                  >
                    {column.render('Header')}
                    <button onClick={() => handleSort(column.accessor)}>
                      <img src={sortIcon} alt="Sort Icon" />
                    </button>
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map((row: any) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()} style={{ borderBottom: '1px solid #e0e0e0' }}>
                  {row.cells.map((cell: any) => (
                    <td
                      {...cell.getCellProps()}
                      style={{ padding: '10px' }}
                    >
                      {cell.render('Cell')}
                    </td>
                  ))}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <footer style={{ background: '#333', padding: '10px', color: '#fff', textAlign: 'center' }}>
        <p>&copy; {new Date().getFullYear()} Your Company Name. All rights reserved.</p>
      </footer>
    </div>

  );
};
